import React, { useState } from 'react';
import moment from 'moment'


const Break = () => {
    const [breakLengthSec,setbreakLength] = useState(300)

    const decrementLength = () => {
        const newLength = breakLengthSec - 60;
        if(newLength<=0){
            setbreakLength(0)
        }
        else{
            setbreakLength(newLength)
        }
    }
    const incrementLength = () => {
        setbreakLength(breakLengthSec + 1);
    }

    const breakLengthMin =  moment.duration(breakLengthSec,'s').minutes();


    return(
        <div >
            <p id="break-label">Break</p>
            <p id="break-length">{breakLengthMin}</p>
            <button onClick={incrementLength}>Increment</button>
            <button onClick={decrementLength}>Decrement</button>

        </div>

    )

}

export default Break;
