import React, {useState} from 'react';
import Header from './Header';
import '../App.css';
import Break from './Break';
import Session from './Session';
import TimeLeft from './TimeLeft';

function App() {
  const [sessionLengthSec,setsessionLength] = useState(60*25)
  const [breakLengthSec,setbreakLength] = useState(300)
  useEffect(()=>{
    setsessionLength(sessionLengthSec)
    setbreakLength(breakLengthSec)
},[sessionLengthSec,breakLengthSec]);

  const decrementSessionLength = () => {
      const newLength = sessionLengthSec - 60;
      if(newLength<=0){
          setsessionLength(0)
      }
      else{
          setsessionLength(newLength)
      }
  }
  const incrementSessionLength = () => {
      setsessionLength(sessionLengthSec + 60);
  }


  const decrementLength = () => {
      const newLength = breakLengthSec - 60;
      if(newLength<=0){
          setbreakLength(0)
      }
      else{
          setbreakLength(newLength)
      }
  }
  const incrementLength = () => {
      setbreakLength(breakLengthSec + 60);
  }

  return (
    <div className="App">
        <Header title="Pomodoro Clock"/>
        <Break breakLengthSec={breakLengthSec} incrementLength={incrementLength} decrementLength={decrementLength} />
        <TimeLeft breakLengthSec = {breakLengthSec} sessionLengthSec={sessionLengthSec}/>
        <Session sessionLengthSec={sessionLengthSec} decrementSessionLength={decrementSessionLength} incrementSessionLength={incrementSessionLength}/>
    </div>
  );
}

export default App;
