import React, { useState, useEffect } from 'react';
import moment from 'moment';
import momentDurationFormatSetup from "moment-duration-format";
momentDurationFormatSetup(moment);


const TimeLeft = ({breakLengthSec,sessionLengthSec,setsessionLength,setbreakLength}) => {
    const [currentSession,setCurrentSession] = useState("Session");
    const [intervalId,setIntervalId] = useState(null);
    const [timeLeft, setTimeLeft] = useState(sessionLengthSec);

    useEffect(()=>{
        setTimeLeft(sessionLengthSec)
    },[sessionLengthSec]);

    const isStart = intervalId !== null;
    const startTimer = () => {
            if(isStart){
                clearInterval(intervalId);
                setIntervalId(null)
            }
            else{
            const newInterval =  setInterval(() => {
                setTimeLeft(prevState => {
                    const newValue = prevState-1;
                    if(newValue>=0)
                        return newValue;
                    if(currentSession == "Session"){
                        setCurrentSession("Break");
                        setTimeLeft(breakLengthSec)
                    }
                    else if(currentSession == "Break"){
                        setCurrentSession("Session");
                        setTimeLeft(sessionLengthSec)
                        }
                    });
                }, 100);
                setIntervalId(newInterval)
            }
    }
    const reset = () => {
        if(!isStart){
            setbreakLength(300);
            setsessionLength(60*25);
            setTimeLeft(60*25);
        }
    }

    const formattedTimeLeft = moment.duration(timeLeft, 's').format('mm:ss');
    return(
     <div className="border">
        <p id="timer-label">{currentSession}</p>
        <p id="time-left">{formattedTimeLeft}</p>
        <button type="button" onClick={startTimer}>{isStart ? 'Stop' : 'Start'}</button>
        <button type="button" onClick={reset}>Reset</button>

    </div>
    )

}
export default TimeLeft;
