import React, { useState } from 'react';
import moment from 'moment'


const Session = () => {
    const [sessionLengthSec,setsessionLength] = useState(60*25)

    const decrementLength = () => {
        const newLength = sessionLengthSec - 60;
        if(newLength<=0){
            setsessionLength(0)
        }
        else{
            setsessionLength(newLength)
        }
    }
    const incrementLength = () => {
        setsessionLength(sessionLengthSec + 60);
    }

    const sessionLengthMin =  moment.duration(sessionLengthSec,'s').minutes();


    return(
        <div >
            <p id="session-label">session</p>
            <p id="session-length">{sessionLengthMin}</p>
            <button onClick={incrementLength}>Increment</button>
            <button onClick={decrementLength}>Decrement</button>

        </div>

    )

}

export default Session;
