import React from 'react';
import moment from 'moment'


const Break = ({breakLengthSec,incrementLength,decrementLength}) => {

    const breakLengthMin =  moment.duration(breakLengthSec,'s').minutes();


    return(
        <div className="row">
            <div className="col-md-4">
            <h2 id="break-label">Break</h2>
            <button onClick={incrementLength}>+</button>
            <p id="break-length">{breakLengthMin}</p>
            <button onClick={decrementLength}>-</button>
            </div>
        </div>

    )

}

export default Break;
